      //         return element.name === member.login;
      //       });
      //       if (mem === undefined) {
      //         saveMember({
      //           name: member.login,
      //           followStatus: false,
      //         });
      //       }
      //     });
      //   }
      setIsLoading(true);
      // });
    });
  }, []);

  return (
    <div>
      <h1>EmberJS Members</h1>
      <div className="container mt-3">
        <Member
          members={members}
          following={following}
          isLoading={isLoading}
          setFollowing={setFollowing}
          setIsLoading={setIsLoading}
        />
      </div>
    </div>
  );
}

export default App;
