import React, { useEffect, useState } from "react";
import { fetchFollowing, fetchMembers, saveMember } from "./api";
import "./App.css";
import Member from "./Member";

function App() {
  const [members, setMembers] = useState([]);
  const [following, setFollowing] = useState();
  const [isLoading, setIsLoading] = useState(false);
  useEffect(() => {
    fetchMembers().then((members) => {
      // split them into groups of 3 (nested arrays)
      let groups = [],
        i = 0,
        n = members.length;
      while (i < n) {
        groups.push(members.slice(i, (i += 3)));
      }
      setMembers(groups);
      fetchFollowing().then((following) => {
        setFollowing(following);
        if (following.length !== members.length) {
          console.log(following);
          members.forEach((member) => {
            const mem = following.find((element) => {
              return element.name === member.login;
            });
            if (mem === undefined) {
              saveMember({
                name: member.login,
                followStatus: false,
              });
            }
          });
        }
        setIsLoading(true);
      });
    });
  }, []);

  return (
    <div>
      <h1>EmberJS Members</h1>
      <div className="container mt-3">
        <Member members={members} following={following} isLoading={isLoading} />
      </div>
    </div>
  );
}

export default App;
