import React, { useState } from "react";
import { fetchDetails, fetchRepos, updateFollowing } from "./api";
import "./App.css";
import DetailsModal from "./DetailsModal";
import ReposModal from "./ReposModal";

export default function Member({
  members,
  following,
  isLoading,
  setFollowing,
}) {
  const [isDetailsModalShown, setIsDetailsModalShown] = useState(false);
  const [isReposModalShown, setIsReposModalShown] = useState(false);
  const [name, setName] = useState("");
  const [details, setDetails] = useState();
  const [repos, setRepos] = useState();

  function showDetailsModal(name, url) {
    setName(name);
    fetchDetails(url).then((details) => {
      setDetails(details);
      setIsDetailsModalShown(true);
    });
  }
  function hideDetailsModal() {
    setIsDetailsModalShown(false);
  }
  function showReposModal(name, url) {
    setName(name);
    fetchRepos(url).then((repos) => {
      setRepos(repos);
      setIsReposModalShown(true);
    });
  }
  function hideReposModal() {
    setIsReposModalShown(false);
  }

  function changeFollowStatus(member) {
    const data = following.find((mem) => {
      return mem.name === member.login;
    });
    console.log(data);
    data.followStatus = !data.followStatus;
    updateFollowing(data).then(() => {
      const updated = following.map((mem) => {
        if (mem.name === member.login) {
          return {
            id: mem.id,
            followStatus: data.followStatus,
            name: mem.name,
          };
        }
      });
      setFollowing(updated);
    });
  }

  function findFollowingMember(member) {
    if (isLoading) {
      const mem = following.find((element) => {
        return element.name === member.login;
      });
      if (mem === undefined) {
        return <div>Follow</div>;
      } else if (mem.followStatus === false) {
        return <div>Follow</div>;
      } else {
        return <div>Unfollowed</div>;
      }
    } else {
      return <div>Follow</div>;
    }
  }

  return (
    <div>
      {isDetailsModalShown && (
        <DetailsModal
          onClose={hideDetailsModal}
          name={name}
          details={details}
        />
      )}
      {isReposModalShown && (
        <ReposModal onClose={hideReposModal} name={name} repos={repos} />
      )}
      {members.map((group, index) => {
        return (
          <div className="row justify-content-center mb-3" key={index}>
            {group.map((member) => {
              return (
                <div className="col-md text-center" key={member.login}>
                  <img
                    src={member.avatar_url}
                    alt={member.login}
                    width="70%"
                    onClick={() => showDetailsModal(member.login, member.url)}
                    id="member-img"
                  ></img>
                  <div
                    onClick={() => showDetailsModal(member.login, member.url)}
                    id="member-name"
                  >
                    {member.login}
                  </div>
                  <div className="btn-toolbar justify-content-center">
                    <button
                      type="button"
                      className="btn btn-outline-info btn-sm mr-3"
                      onClick={() =>
                        showReposModal(member.login, member.repos_url)
                      }
                    >
                      Repos
                    </button>
                    <button
                      type="button"
                      className="btn btn-outline-primary btn-sm"
                      onClick={() => changeFollowStatus(member)}
                    >
                      {findFollowingMember(member)}
                      {/* {followStatusList[index].followStatus === true && (
                        <div>Unfollow</div>
                      )}
                      {followStatusList[index].followStatus === false && (
                        <div>Follow</div>
                      )} */}
                    </button>
                  </div>
                </div>
              );
            })}
          </div>
        );
      })}
    </div>
  );
}
