import React, { useState } from "react";
import { fetchDetails } from "./api";
import "./App.css";
import DetailsModal from "./DetailsModal";

export default function Member({ members }) {
  const [isModalShown, setIsModalShown] = useState(false);
  const [name, setName] = useState("");

  function showModal(name, url) {
    setName(name);
    fetchDetails(url).then((details) => {
      console.log(details);
    });
    setIsModalShown(true);
  }
  function hideModal() {
    setIsModalShown(false);
  }

  return (
    <div>
      {isModalShown && <DetailsModal onClose={hideModal} name={name} />}
      {members.map((group, index) => {
        return (
          <div className="row justify-content-center mb-3" key={index}>
            {group.map((member) => {
              return (
                <div className="col-md text-center" key={member.login}>
                  <img
                    src={member.avatar_url}
                    alt={member.login}
                    width="70%"
                    onClick={() => showModal(member.login, member.url)}
                    id="member-img"
                  ></img>
                  <div
                    onClick={() => showModal(member.login, member.url)}
                    id="member-name"
                  >
                    {member.login}
                  </div>
                </div>
              );
            })}
          </div>
        );
      })}
    </div>
  );
}
