import React, { useState } from "react";
import "./App.css";
import DetailsModal from "./DetailsModal";

export default function Member({ members }) {
  const [isModalShown, setIsModalShown] = useState(false);

  function showModal() {
    setIsModalShown(true);
  }
  function hideModal() {
    setIsModalShown(false);
  }

  return (
    <div>
      {isModalShown && <DetailsModal onClose={hideModal} />}
      {members.map((group, index) => {
        return (
          <div className="row justify-content-center mb-3" key={index}>
            {group.map((member) => {
              return (
                <div className="col-md text-center" key={member.login}>
                  <img
                    src={member.avatar_url}
                    alt={member.login}
                    width="70%"
                    onClick={showModal}
                    id="member-img"
                  ></img>
                  <div onClick={showModal()} id="member-name">
                    {member.login}
                  </div>
                </div>
              );
            })}
          </div>
        );
      })}
    </div>
  );
}
